mapper.select:
select

  distinct


from userinfor




  order by ${orderByClause}


  limit ${currPage},${limit}

mapper.insert:
insert into userinfor (user_id, user_name, user_sex, 
  user_work, user_contact, user_address, 
  user_marriage, user_department, user_employTime, 
  user_password, user_education, user_loginName
  )
values (#{user_id,jdbcType=INTEGER}, #{user_name,jdbcType=VARCHAR}, #{user_sex,jdbcType=BIT}, 
  #{user_work,jdbcType=VARCHAR}, #{user_contact,jdbcType=VARCHAR}, #{user_address,jdbcType=VARCHAR}, 
  #{user_marriage,jdbcType=BIT}, #{user_department,jdbcType=INTEGER}, #{user_employTime,jdbcType=TIMESTAMP}, 
  #{user_password,jdbcType=VARCHAR}, #{user_education,jdbcType=VARCHAR}, #{user_loginName,jdbcType=VARCHAR}
  )
